import React, {useState } from "react";
import * as BooksAPI from "./utils/BooksAPI";
import ListBooks from "./ListBooks";
import OpenSearch from "./OpenSearch"

const Home = () => {


  const [books, setBooks] = useState([])

  useEffect(() => {
    BooksAPI.getAll().then((books) => {
      this.setState({ books });
    });
  }, [])

  const updateBook = (book, newShelf) => {

    BooksAPI.update(book, newShelf).then(BooksAPI.getAll()).then((books) => {
      this.setState({ books });
    });
  }

  listCurrentlyReading = () =>{

  }


    return (
      <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <ListBooks
            onChangeBooks={(book, newShelf) =>
              this.updateBook(book, newShelf)
            }
            bookshelfTitle={"wantToRead"}
            books={this.state.books}
          />
          <ListBooks
            onChangeBooks={(book, newShelf) =>
              this.updateBook(book, newShelf)
            }
            bookshelfTitle={"currentlyReading"}
            books={this.state.books}
          />
          <ListBooks
            onChangeBooks={(book, newShelf) =>
              this.updateBook(book, newShelf)
            }
            bookshelfTitle={"read"}
            books={this.state.books}
          />
        </div>
        <OpenSearch /> 
      </div>   
    );
  }


export default Home;
