import React,{useState} from "react";
import * as BooksAPI from "./utils/BooksAPI";

const Book = (props) => {

    const updateBook = (book, newShelf, id) => {
        BooksAPI.update(book, newShelf).then(() => {
          let elementsIndex = this.state.books.findIndex(
            (element) => element.id === id
          );
          let newArray = [...props.list];
          newArray[elementsIndex] = { ...newArray[elementsIndex], shelf: newShelf };
          this.setState({ books: newArray });
          this.setState({ shelf: newShelf });
        });
      }


    const [shelf, setShelf] = useState("read")
    const handleChange = (e, book, id) => {
    let newShelft = e.target.value;
    setShelf(newShelft);
    updateBook(book, shelf, id); 
 };


return (
    <div className="book">
    <div
      className="book-cover"
      style={{
        width: 128,
        height: 193,
        backgroundImage: `url("${props.book.imageLinks.thumbnail}")`,
      }}
    />
   <div className="">
       <select
            value={shelf}   
            onChange={(e) =>
              handleChange(e,props.book, props.book.id)
            }     
          >
          <option value="move" disabled>Move to...</option>
          <option value="currentlyReading">Currently Reading</option>
          <option value="wantToRead">Want to Read</option>
          <option value="read">Read</option>
          <option value="none">None</option>
          </select>
      </div>
    <div className="book-title">{props.book.tittle}</div>
    <div className="book-authors">{props.book.authors}</div>
  </div>  
  )

}


export default Book;
