import React,{useState} from "react";
import * as BooksAPI from "./utils/BooksAPI";


const Book = (props) => {
    const [shelf, setShelf] = useState(props.book.shelf)


    const updateBook = (book, shelf) =>{
      BooksAPI.update(book, shelf)
    }

    const handleChange = (e, book, id) => {
    let newBooks = [...props.list]
    let newShelft = e.target.value;
    props.changeAllBooks(newBooks)
    setShelf(newShelft);
    updateBook(book,newShelft)
    console.log(newBooks)   
 };


return (
    <div className="book">
      <div className="book-top">
    <div
      className="book-cover"
      style={{
        width: 128,
        height: 193,
        backgroundImage: `url("${props.book.imageLinks.thumbnail}")`,
      }}
    />
   <div className="book-shelf-changer">
       <select
            value={shelf}   
            onChange={(e) =>
              handleChange(e,props.book, props.book.id)
            }     
          >
          <option value="move" disabled>Move to...</option>
          <option value="currentlyReading">Currently Reading</option>
          <option value="wantToRead">Want to Read</option>
          <option value="read">Read</option>
          <option value="none">None</option>
          </select>
      </div>
      </div>
    <div className="book-title">{props.book.title}</div>
    <div className="book-authors">{props.book.authors}</div>
  </div>  
  )

}


export default Book;
